<?php
require_once 'vendor/autoload.php';
require_once 'src/CognitoSMSPasswordless.php';
$cognito = new DingoTipPOC\CognitoSMSPasswordless();
?>
<?php
if(!isset($_GET['mobile'])):
?>
<html>
<head>
</head>
<body>
<img src="data:image/jpeg;base64,"/>
<?php endif;


if(isset($_GET['verify'])): ?>
    <form>
        <input type="text" name="code" placeholder="Enter your code"/>
        <input type="hidden" name="number" value="<?php echo  $_GET['mobile'];?>"/>
        <input type="hidden" name="session" value="<?php echo  $_GET['session'];?>"/>
        <input type="submit"/>
    </form>

<?php
elseif(isset($_GET['mobile'])):
    $session = $cognito->initAuth($_GET['mobile']);
    $cognito->redirectTo('verify',['mobile' => $_GET['mobile'],'session' => $session]);

elseif(isset($_GET['code'])):
    $response = $cognito->verifyCode($_GET['code'],$_GET['number'],$_GET['session']);
?>
<h2> Authentication successful:</h2>
<p>Access Token: <?php echo $response->get('AuthenticationResult')['AccessToken'] ?></p>
<p>Refresh Token: <?php echo $response->get('AuthenticationResult')['RefreshToken'] ?></p>

<?php else: ?>
    <form>
        <input type="text" name="mobile" placeholder="Enter your mobile"/>
        <input type="submit"/>
    </form>
<?php endif; ?>





</body>
</html>